package guards_examples_test

import (
	"fmt"
	"github.com/iotaledger/iota.go/guards"
)

// i req: trytes, The Trytes to check.
// o: bool, Whether the Trytes are valid.
func ExampleIsTrytes() {
	fmt.Println(guards.IsTrytes("ABCD")) // output: true
}

// i req: trytes, The Trytes to check.
// i req: length, The length the Trytes must have.
// o: bool, Whether it passes the check.
func ExampleIsTrytesOfExactLength() {
	fmt.Println(guards.IsTrytesOfExactLength("ABCD", 4)) // output: true
	fmt.Println(guards.IsTrytesOfExactLength("ABCD", 2)) // output: false
}

// i req: trytes, The Trytes to check.
// i req: max, The max length the Trytes can have.
// o: bool, Whether it passes the check.
func ExampleIsTrytesOfMaxLength() {
	fmt.Println(guards.IsTrytesOfMaxLength("ABCD", 5)) // output: true
	fmt.Println(guards.IsTrytesOfMaxLength("ABCD", 2)) // output: false
}

// i req: trytes, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsEmptyTrytes() {
	fmt.Println(guards.IsEmptyTrytes("99999999")) // output: true
	fmt.Println(guards.IsEmptyTrytes("ABCD"))     // output: false
}

// i req: trytes, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsHash() {
	hash := "ZFPPXWSTIYJCPPMVCCBZR9TISFJALXEXVYMADGTERQLTHAZJMHGWWFIXVCVPJRBUYLKMTLLKMTWMA9999"
	fmt.Println(guards.IsHash(hash)) // output: true
}

// i req: addr, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsAddressWithChecksum() {
	addr := "ORQMMP9NVIHMPAOGRLTGIYJEKPNWOKQQKRTBFZ9HVYK9YFCGETUMEAPDSWDVADPYAVZAUIWYMQMUQABIDERPEHEHOZ"
	fmt.Println(guards.IsAddressWithChecksum(addr)) // output: true
}

// i req: trytes, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsTransactionHash() {
	hash := "ZFPPXWSTIYJCPPMVCCBZR9TISFJALXEXVYMADGTERQLTHAZJMHGWWFIXVCVPJRBUYLKMTLLKMTWMA9999"
	fmt.Println(guards.IsTransactionHash(hash)) // output: true
}

// i req: trytes, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsTransactionHashWithMWM() {
	hash := "ZFPPXWSTIYJCPPMVCCBZR9TISFJALXEXVYMADGTERQLTHAZJMHGWWFIXVCVPJRBUYLKMTLLKMTWMA9999"
	fmt.Println(guards.IsTransactionTrytesWithMWM(hash, 14)) // output: true
}

// i req: trytes, The Trytes to check.
// o: bool, Whether it passes the check.
func ExampleIsTag() {
	tag := "CD9999999999999999999999999"
	fmt.Println(guards.IsTag(tag)) // output: true
}

